import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';
import { toast } from 'react-toastify';
import { 
  ArrowUpIcon, 
  ArrowDownIcon, 
  ArrowsRightLeftIcon, 
  ExclamationTriangleIcon,
  FunnelIcon
} from '@heroicons/react/24/outline';

const Transactions = () => {
  const [transactions, setTransactions] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    fetchTransactions();
  }, [currentPage, filter]);

  const fetchTransactions = async () => {
    try {
      setIsLoading(true);

      let url = `/transactions?page=${currentPage}&limit=10`;
      if (filter) {
        url += `&type=${filter}`;
      }

      const response = await api.get(url);

      setTransactions(response.data.transactions);
      setTotalPages(response.data.totalPages);

      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching transactions:', error);
      toast.error('Failed to load transactions');
      setIsLoading(false);
    }
  };

  // Helper function to format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
    }).format(amount);
  };

  // Helper function to format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  // Helper function to get transaction icon
  const getTransactionIcon = (type, status) => {
    if (status === 'flagged') {
      return (
        <div className="flex-shrink-0 rounded-md bg-yellow-100 p-2">
          <ExclamationTriangleIcon className="h-5 w-5 text-yellow-600" />
        </div>
      );
    }

    switch (type) {
      case 'deposit':
        return (
          <div className="flex-shrink-0 rounded-md bg-green-100 p-2">
            <ArrowDownIcon className="h-5 w-5 text-green-600" />
          </div>
        );
      case 'withdrawal':
        return (
          <div className="flex-shrink-0 rounded-md bg-red-100 p-2">
            <ArrowUpIcon className="h-5 w-5 text-red-600" />
          </div>
        );
      case 'transfer':
        return (
          <div className="flex-shrink-0 rounded-md bg-blue-100 p-2">
            <ArrowsRightLeftIcon className="h-5 w-5 text-blue-600" />
          </div>
        );
      default:
        return null;
    }
  };

  const handleFilterChange = (newFilter) => {
    // If clicking the current filter, clear it
    if (newFilter === filter) {
      setFilter('');
    } else {
      setFilter(newFilter);
    }
    setCurrentPage(1); // Reset to first page when changing filters
  };

  return (
    <div className="py-6">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between">
          <h1 className="text-2xl font-semibold text-gray-900">Transaction History</h1>
          <Link
            to="/transactions/new"
            className="inline-flex items-center rounded-md border border-transparent bg-primary-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
          >
            New Transaction
          </Link>
        </div>

        {/* Filters */}
        <div className="mt-4 flex space-x-2">
          <div className="flex items-center text-sm text-gray-500">
            <FunnelIcon className="mr-1 h-4 w-4" />
            <span>Filter:</span>
          </div>
          <button
            onClick={() => handleFilterChange('deposit')}
            className={`rounded-md px-3 py-1 text-sm ${
              filter === 'deposit' 
                ? 'bg-green-100 text-green-800' 
                : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
            }`}
          >
            Deposits
          </button>
          <button
            onClick={() => handleFilterChange('withdrawal')}
            className={`rounded-md px-3 py-1 text-sm ${
              filter === 'withdrawal' 
                ? 'bg-red-100 text-red-800' 
                : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
            }`}
          >
            Withdrawals
          </button>
          <button
            onClick={() => handleFilterChange('transfer')}
            className={`rounded-md px-3 py-1 text-sm ${
              filter === 'transfer' 
                ? 'bg-blue-100 text-blue-800' 
                : 'bg-gray-100 text-gray-800 hover:bg-gray-200'
            }`}
          >
            Transfers
          </button>
        </div>

        {/* Transactions List */}
        <div className="mt-4 overflow-hidden bg-white shadow sm:rounded-lg">
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-primary-600"></div>
            </div>
          ) : transactions.length > 0 ? (
            <>
              <ul className="divide-y divide-gray-200">
                {transactions.map((transaction) => (
                  <li key={transaction.id} className="px-4 py-4 sm:px-6 hover:bg-gray-50">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        {getTransactionIcon(transaction.type, transaction.status)}
                        <div className="ml-4">
                          <p className="font-medium text-gray-900 capitalize">
                            {transaction.type} {transaction.status === 'flagged' && '(Flagged)'}
                          </p>
                          <p className="text-sm text-gray-500">
                            {transaction.description || `${transaction.type} transaction`}
                          </p>
                          <p className="text-xs text-gray-400">{formatDate(transaction.createdAt)}</p>
                        </div>
                      </div>
                      <div className="flex flex-col items-end">
                        <p className={`text-sm font-medium ${
                          transaction.type === 'deposit' ? 'text-green-600' : 
                          transaction.type === 'withdrawal' ? 'text-red-600' : 'text-blue-600'
                        }`}>
                          {transaction.type === 'deposit' ? '+' : '-'}{formatCurrency(transaction.amount)}
                        </p>
                        {transaction.status === 'flagged' && (
                          <span className="mt-1 inline-flex items-center rounded-full bg-yellow-100 px-2.5 py-0.5 text-xs font-medium text-yellow-800">
                            Fraud Alert
                          </span>
                        )}
                      </div>
                    </div>
                  </li>
                ))}
              </ul>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="flex items-center justify-between border-t border-gray-200 bg-white px-4 py-3 sm:px-6">
                  <div className="flex flex-1 justify-between sm:hidden">
                    <button
                      onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                      disabled={currentPage === 1}
                      className={`relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 ${
                        currentPage === 1 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-50'
                      }`}
                    >
                      Previous
                    </button>
                    <button
                      onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                      disabled={currentPage === totalPages}
                      className={`relative ml-3 inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 ${
                        currentPage === totalPages ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-50'
                      }`}
                    >
                      Next
                    </button>
                  </div>
                  <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
                    <div>
                      <p className="text-sm text-gray-700">
                        Showing page <span className="font-medium">{currentPage}</span> of{' '}
                        <span className="font-medium">{totalPages}</span>
                      </p>
                    </div>
                    <div>
                      <nav className="isolate inline-flex -space-x-px rounded-md shadow-sm" aria-label="Pagination">
                        <button
                          onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                          disabled={currentPage === 1}
                          className={`relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ${
                            currentPage === 1 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-50'
                          }`}
                        >
                          <span className="sr-only">Previous</span>
                          <svg className="h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                            <path fillRule="evenodd" d="M12.79 5.23a.75.75 0 01-.02 1.06L8.832 10l3.938 3.71a.75.75 0 11-1.04 1.08l-4.5-4.25a.75.75 0 010-1.08l4.5-4.25a.75.75 0 011.06.02z" clipRule="evenodd" />
                          </svg>
                        </button>

                        {/* Page numbers */}
                        {Array.from({ length: Math.min(totalPages, 5) }, (_, i) => {
                          const pageNumber = i + 1;
                          return (
                            <button
                              key={pageNumber}
                              onClick={() => setCurrentPage(pageNumber)}
                              className={`relative inline-flex items-center px-4 py-2 text-sm font-semibold ${
                                pageNumber === currentPage
                                  ? 'z-10 bg-primary-50 border-primary-500 text-primary-600'
                                  : 'text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50'
                              }`}
                            >
                              {pageNumber}
                            </button>
                          );
                        })}

                        <button
                          onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                          disabled={currentPage === totalPages}
                          className={`relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ${
                            currentPage === totalPages ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-50'
                          }`}
                        >
                          <span className="sr-only">Next</span>
                          <svg className="h-5 w-5" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" aria-hidden="true">
                            <path fillRule="evenodd" d="M7.21 14.77a.75.75 0 01.02-1.06L11.168 10 7.23 6.29a.75.75 0 111.04-1.08l4.5 4.25a.75.75 0 010 1.08l-4.5 4.25a.75.75 0 01-1.06-.02z" clipRule="evenodd" />
                          </svg>
                        </button>
                      </nav>
                    </div>
                  </div>
                </div>
              )}
            </>
          ) : (
            <div className="px-4 py-12 text-center">
              <p className="text-gray-500 mb-4">No transactions found</p>
              {filter && (
                <p className="text-sm text-gray-400 mb-4">
                  Try removing the filter or create a new transaction
                </p>
              )}
              <Link
                to="/transactions/new"
                className="inline-flex items-center rounded-md border border-transparent bg-primary-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-primary-700 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
              >
                Create New Transaction
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Transactions;
